   {
     if (typeof(crmForm.all.customerid.DataValue[0].name) != "undefined")
     {      
      var ___SalesHeaderDiscountCode = 10000;
      var url = "/_controls/lookup/lookupsingle.aspx?objecttypes=" + ___SalesHeaderDiscountCode + "&browse=1&searchvalue=" + crmForm.all.customerid.DataValue[0].name;

       var detailedamount = LocStringToFloat(crmForm.all.totallineitemamount.value.replace(ORG_CURRENCY_SYMBOL," "));

       var returnValue =  window.showModalDialog(url);	

       if (returnValue != null)
       {         
          var discountpercents = returnValue.items[0].keyValues["new_discountpercents"].value;

          var minamont = LocStringToFloat(returnValue.items[0].keyValues["new_minimumamount"].value.replace(ORG_CURRENCY_SYMBOL," "));

         if( detailedamount> minamont)
         {
                    if (confirm("Apply discount?" ,"Yes", "No"))
                   {                        
                        crmForm.all.discountpercentage.DataValue = LocStringToFloat(discountpercents) ;
                    }
          }

      }

    }        
   }
};

















