event.script:
//            ___Discount Package 3.0___

var productGroupFlag = crmForm.all.new_productgroupflag;
if(productGroupFlag != null)
{       
     if (crmForm.all.new_productgroupflag.DataValue)
    {
         crmForm.all.new_relatedproductid.disabled= false;
         crmForm.all.new_productdiscountgroupid.disabled =true;
         crmForm.all.new_productdiscountgroupid.DataValue = null;
    }
   else
   { 
         crmForm.all.new_relatedproductid.disabled= true;
         crmForm.all.new_productdiscountgroupid.disabled =false;
         crmForm.all.new_relatedproductid.DataValue = null;
    }
}

//     ___Discount Package 3.0___

var customerGroupFlag = crmForm.all.new_customergroupflag;
if(customerGroupFlag != null)
{       
     if (customerGroupFlag.DataValue)
    {
         crmForm.all.new_parentaccountid.disabled= false;
         crmForm.all.new_customerdiscountgroupid.disabled =true;
         crmForm.all.new_customerdiscountgroupid.DataValue = null;
    }
   else
   { 
         crmForm.all.new_parentaccountid.disabled= true;
         crmForm.all.new_customerdiscountgroupid.disabled =false;
         crmForm.all.new_parentaccountid.DataValue = null;
    }
}

//          ___Discount Package 3.0___

if( ((crmForm.all.new_parentaccountid == null)||(crmForm.all.new_parentaccountid.DataValue == null))
  &amp;&amp; ((crmForm.all.new_customerdiscountgroupid == null)||(crmForm.all.new_customerdiscountgroupid.DataValue == null)))
{
   alert("Enter Customer information !");
   event.returnValue=false;
}

if (((crmForm.all.new_relatedproductid == null)||(crmForm.all.new_relatedproductid.DataValue == null))
&amp;&amp;((crmForm.all.new_productdiscountgroupid == null)||(crmForm.all.new_productdiscountgroupid.DataValue== null)))
{
  alert("Enter Product information !");
  event.returnValue=false;
}

//                    ___Discount Package 3.0___

//1__ Draw button to call _____LOOKUP  UOM______

var uomFld = document.getElementById("new_uomname");
if(uomFld  != null)
uomFld.outerHTML = 
'&lt;table width=\"100%\"&gt;&lt;tr&gt; &lt;td width=\"90%\" &gt;'+ uomFld.outerHTML+'&lt;/td&gt; &lt;td&gt;&lt;input type=button value=\"...\"  name=\"uom_viewer\" &gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;';

var returnVal = "";
var url = '/_controls/lookup/lookupsingle.aspx?objecttypes=1055&amp;browse=0';

if(document.getElementById("uom_viewer") != null)
{
 document.getElementById("uom_viewer").onclick = function()
 {              
             returnVal = window.showModalDialog(url);
             if(returnVal != null)
                  crmForm.all.new_uomname.value = returnVal.items[0].keyValues["name"].value;
 }
}

//2___ENABLED - DISABLED product/ customer fields

 var productGroupFlag = crmForm.all.new_productgroupflag;
 if(productGroupFlag != null)
 {     
    if(crmForm.FormType == 1)   crmForm.all.new_productgroupflag.DataValue = true;

    if(crmForm.all.new_productdiscountgroupid != null)
   {
        if(crmForm.all.new_productgroupflag.DataValue)
       {
               crmForm.all.new_relatedproductid.disabled= false;
               crmForm.all.new_productdiscountgroupid.disabled =true;
               crmForm.all.new_productdiscountgroupid.DataValue = null;            
        }
       else
       { 
                 crmForm.all.new_relatedproductid.disabled= true;                      
                 crmForm.all.new_productdiscountgroupid.disabled =false;
                 crmForm.all.new_relatedproductid.DataValue = null;          
       }
   }

 }

 var customerGroupFlag = crmForm.all.new_customergroupflag;

 if(customerGroupFlag != null)
 {
     if(crmForm.FormType == 1)  crmForm.all.new_customergroupflag.DataValue = true;

    if(crmForm.all.new_customerdiscountgroupid != null) 
    { 
       if (customerGroupFlag.DataValue)
      {
            crmForm.all.new_parentaccountid.disabled= false;
            crmForm.all.new_customerdiscountgroupid.disabled =true;
            crmForm.all.new_customerdiscountgroupid.DataValue = null;
      }
     else
     { 
            crmForm.all.new_parentaccountid.disabled= true;
            crmForm.all.new_customerdiscountgroupid.disabled =false;
            crmForm.all.new_parentaccountid.DataValue = null;
      }
   }

 }
