	else
	{
		var productIdControl1 = crmForm.all.tcc_erp_product_id_control;

		if (null != productIdControl1)
		{
			productIdControl1.DataValue = null;
		}

		var productControl1 = crmForm.all.tcc_erp_product_control;

		if (null != productControl1)
		{
			productControl1.DataValue = null;
		}
	}
}

