	{
		var selectedValue1 = crmForm.all.address1_freighttermscode.DataValue;
		var newOptions1 = new Array();
		var initialOptions1 = crmForm.all.address1_freighttermscode.Options;
		var pickLength1 = initialOptions1.length;
		var j1 = 0;

		for(var i = 0; i&lt;pickLength1; i++)
		{
			crmForm.all.address1_freighttermscode.DeleteOption(initialOptions1[i].DataValue);	

			if (initialOptions1[i].Text.indexOf(businessUnitText) != 0)
			{
				if (initialOptions1[i].Text.length == 0)
				{
					newOptions1[j1] = initialOptions1[i];
					j1 = j1 + 1;
				}
			}
			else
			{
				var opti1 = new Object();

				var oldText1 = initialOptions1[i].Text;
				var newText1 = oldText1.substring(businessUnitText.length, oldText1.length);
				opti1.Text = newText1;
				opti1.DataValue = initialOptions1[i].DataValue;
				newOptions1[j1] = opti1;
				j1 = j1 + 1;
			}
		}

		crmForm.all.address1_freighttermscode.Options = newOptions1;
		crmForm.all.address1_freighttermscode.DataValue = selectedValue1;
		crmForm.all.address1_freighttermscode.disabled = false;
	}

