		if ((rtypecontrolValue != 3)&amp;&amp;(rtypecontrolValue != 11))
		{
			alert('Please select correct RelationshipType');
			event.returnValue = false;
		}
	}

	var ownercontrol = crmForm.all.tcc_erp_owner_control;
	var erpcontrol = crmForm.all.tcc_erp_control;

	var checkPassed = 0;

	if ((null != ownercontrol)&amp;&amp;(null != erpcontrol))
	{
		var ownercontrolValue = ownercontrol.DataValue;
		var erpcontrolValue = erpcontrol.DataValue;

		if ((null != ownercontrolValue)&amp;&amp;(null != erpcontrolValue)&amp;&amp;('' != ownercontrolValue)&amp;&amp;('' != erpcontrolValue))
		{
			var preparedErpControlValue = '{' + erpcontrolValue + '}';
			var preparedOwnerControlValue = ownercontrolValue.toLowerCase();

			if (preparedErpControlValue != preparedOwnerControlValue)
			{
				checkPassed = -1;
			}
			else
			{
				checkPassed = 1;
			}
		}
	}

	if (0 == checkPassed)
	{
		event.returnValue = false;
	}
	else if (-1 == checkPassed)
	{
		alert('Owner does not correspond to sync. BU');
		event.returnValue = false;	
	}
}

