						tccErpControl.FireOnChange();
					}

					var tccErpPriceListControl = parentCrmFormAll.tcc_erp_pricelist_control;

					if (null != tccErpPriceListControl)
					{
						tccErpPriceListControl.DataValue = crmForm.all.tcc_pricelist.DataValue;
						tccErpPriceListControl.FireOnChange();
					}
				}
			}
		}
	}
}

/* lookup uom schedule; */
var uomscheduleid;

/* lookup uom schedule; */
var lookupItems = LookupObjects( crmForm.defaultuomscheduleid.getLookupField(), 'single', 'UomSchedule', '1056', 0, null, null, null, null, false);

if (!IsNull(lookupItems) && !IsNull(lookupItems.items))
{
	crmForm.all.defaultuomscheduleid.Clear();
	crmForm.all.defaultuomscheduleid.AddItems(lookupItems.items);
	if(lookupItems.items.length == 0)
	{
		return false;
	}
}
else
{
	/* prevent the uom lookup from launching; */
	return false;
}

