if (null != parent)
{
	if (null != parent.document)
	{
		if (null != parent.document.forms)
		{
			var parentCrmForm = parent.document.forms[0];

			if (null != parentCrmForm)
			{
				var parentCrmFormAll = parentCrmForm.all;

				if (null != parentCrmFormAll)
				{
					var tccErpIdControl = parentCrmFormAll.tcc_erp_product_id_control;

					if (null != tccErpIdControl)
					{
						var prid = crmForm.ObjectId;

						if ((null != prid)&amp;&amp;('' != prid))
						{
							tccErpIdControl.DataValue = prid;
						}
					}

					var tccErpProductControl = parentCrmFormAll.tcc_erp_product_control;

					if (null != tccErpProductControl)
					{
						var businessUnitInfo = crmForm.all.tcc_erp_business_unit;					

						if (null != businessUnitInfo)
						{
							var businessUnitValue = businessUnitInfo.DataValue;

							if (null != businessUnitValue)
							{
								tccErpProductControl.DataValue = businessUnitValue;
								tccErpProductControl.FireOnChange();
							}
						}
					}
				}
			}
		}
	}
}
